script:
if doc.custom_start_time and doc.custom_end_time:
    # Convert to datetime if they're strings
    start = doc.custom_start_time
    end = doc.custom_end_time

    # Use frappe.utils to parse if needed
    if isinstance(start, str):
        start = frappe.utils.get_datetime(start)
    if isinstance(end, str):
        end = frappe.utils.get_datetime(end)

    # Calculate difference
    diff = end - start
    total_seconds = int(diff.total_seconds())

    if total_seconds < 0:
        total_seconds = 0

    # Use Frappe's built-in formatter
    doc.custom_time_worked = frappe.utils.format_duration(total_seconds)
else:
    doc.custom_time_worked = ""

if doc.customer:
    customer_code = frappe.db.get_value('Customer', doc.customer, 'custom_customercode')
    if customer_code:
        doc.custom_customercode = customer_code

# Auto-fill custom_product based on customer selection
if doc.custom_customer_name:
    # Fetch the product name from HD Customer's custom_productname field
    customer = frappe.get_doc("HD Customer", doc.custom_customer_name)
    if customer.custom_productname:
        doc.custom_product = customer.custom_productname

# Auto-populate virtual fields after ticket creation
if doc.custom_customer_name:
    try:
        # Get the customer document
        customer = frappe.get_doc('HD Customer', doc.custom_customer_name)

        # Set customercode
        if hasattr(customer, 'custom_customercode'):
            doc.custom_customercode = customer.custom_customercode or ''

        # Set product
        if hasattr(customer, 'custom_productname'):
            doc.custom_product = customer.custom_productname or ''

        # Save without triggering validation
        doc.save(ignore_permissions=True)

    except Exception as e:
        frappe.log_error(f"Error in HD Ticket auto-populate: {str(e)}")

customer_name = frappe.form_dict.get('customer_name')
updates = frappe.form_dict.get('updates')

# Validate inputs
if not customer_name:
    frappe.response['message'] = {
        "success": False,
        "message": "Customer name is required"
    }
else:
    try:
        # Parse updates if it's a string
        if type(updates).__name__ == 'str':  # Instead of isinstance()
            updates = json.loads(updates)

        # Get the document
        doc = frappe.get_doc("HD Customer", customer_name)

        # Update fields - instead of hasattr, just try to set
        for field, value in updates.items():
            try:
                doc.set(field, value)
            except Exception:
                # Field doesn't exist, skip it
                continue

        # Save document (this triggers your document event script)
        doc.save()

        frappe.response['message'] = {
            "success": True,
            "message": "Customer updated successfully"
        }

    except Exception as e:
        frappe.log_error(frappe.get_traceback(), "Customer Update Error")
        frappe.response['message'] = {
            "success": False,
            "message": str(e)
        }

if doc.custom_product:
    team = frappe.db.get_value("Product", doc.custom_product, "team")
    if team:
        doc.agent_group = team
    else:
        doc.agent_group = None

